        vec4 v2 = texture(cc_jointTexture, vec2((x + 1.5) * invSize, y));
        vec4 v3 = texture(cc_jointTexture, vec2((x + 2.5) * invSize, y));
        return mat4(vec4(v1.xyz, 0.0), vec4(v2.xyz, 0.0), vec4(v3.xyz, 0.0), vec4(v1.w, v2.w, v3.w, 1.0));
